var ServiceNowIncidentUtils = Class.create();
ServiceNowIncidentUtils.prototype = {
	initialize: function() {
	},

	getIncident: function(id) {
		var gr = new GlideRecord("incident");
		if (!gr.get(id))
			return "No incidents were found. Â¿otra p*** vez me has pasado mal el ID?... venga, que no es tan dificil...";

		var str = "<p>sys_id es: '" + gr.getUniqueValue() + "'</p>" +
			"<p>Caller es: '" + gr.getDisplayValue('caller_id') + "'</p>" +
			"<p>Caller.user_name es: '" + gr.caller_id.getRefRecord().getValue('user_name') + "'</p>" +
			"<p>Caller.first_name es: '" + gr.caller_id.getRefRecord().getValue('first_name') + "'</p>" +
			"<p>Number es: '" + gr.getDisplayValue('number') + "'</p>";

		return str;
	},

	type: 'ServiceNowIncidentUtils'
};
