var ServiceNowIncidentUtilsAjax = Class.create();
ServiceNowIncidentUtilsAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getIncident: function() {
		var instance = this.getParameter('sysparm_instance');
		var str = {};
		//gs.addInfoMessage(instance);
		var gr = new GlideRecord("incident");
		if (!gr.get(instance)) {
			str.message = "No incidents were found. ¿otra p*** vez me has pasado mal el ID?... venga, que no es tan dificil leñe...";
			return JSON.stringify(str);
		}

		/*str.message = '<table class="table table-striped table-responsive" ng-if="data.list.length">'+
			'<thead><tr><th class="header-checkbox"><div><input class="pointer" type="checkbox" ng-model="data.allSelected" ng-click="selectAllOnClick()"></div></th><th class="header-{{field}}" ng-repeat="field in data.fields_array track by $index" ng-click="setOrderBy(field)" tabindex="0"><div class="th-title" aria-label="${Sort by } {{data.column_labels[field]}}">{{data.column_labels[field]}}</div><i class="fa" ng-if="field == data.o" aria-label="${Sorted by } {{data.column_labels[field]}}"></i></th></tr></thead>';*/

		str.message ="<p>sys_id es: '" + gr.getUniqueValue() + "'</p>" +
			"<p>Caller es: '" + gr.getDisplayValue('caller_id') + "'</p>" +
			"<p>Caller.user_name es: '" + gr.caller_id.getRefRecord().getValue('user_name') + "'</p>" +
			"<p>Caller.first_name es: '" + gr.caller_id.getRefRecord().getValue('first_name') + "'</p>" +
			"<p>Number es: '" + gr.getDisplayValue('number') + "'</p>";
		//str.message = "andres";
		return JSON.stringify(str);
	},

	type: 'ServiceNowIncidentUtilsAjax'
});
