sys_ui_script.script:
angular.module('angularapp', []);
angular.module('angularapp').controller('AngularAppCtrl', function($scope){
	$scope.myVar = '';
	$scope.panel = '';
	$scope.i = 0;
	$scope.clearTxt = function(){
		$scope.myVar = '';
		$scope.i++;
		alert("the myVar value was cleared " + $scope.i + " times by " + g_user.userName);
	};

	$scope.showIncident = function(incident){
		var ajax = new GlideAjax("ServiceNowIncidentUtils");
		ajax.addParam("sysparm_name", "generateOneTimePassword");
		ajax.addParam("sysparm_incident", incident);
		ajax.getXMLWait(); 
		ajax.getXMLWait();
		$scope.panel = ajax.getAnswer();
	};
});
